INSERT INTO `rw_note_comment` VALUES ('126', '90', '17964', '0', '236', '0', '非常有同感。这些年，少有阅读笔耕，提笔忘字，说话无辞。加入本群后，通过书友推荐的各种简短精髓的阅读，头脑中纵有了千头万绪，期待能早日理顺。', '2016-01-28 07:35:35', '0');
