-- ----------------------------
-- Table structure for `rw_action_config`
-- ----------------------------
DROP TABLE IF EXISTS `rw_action_config`;
CREATE TABLE `rw_action_config` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `action_type` varchar(10) NOT NULL COMMENT 'act_topic话题讨论,act_note提示写笔记,alert,banner广告,topic话题',
  `title` varchar(50) NOT NULL COMMENT '标题',
  `button` varchar(16) NOT NULL COMMENT '按钮内容',
  `href` varchar(500) NOT NULL COMMENT 'url',
  `img` varchar(500) NOT NULL COMMENT '图片url',
  `note_id` int(10) NOT NULL COMMENT '话题id',
  `priority` int(4) NOT NULL COMMENT '优先级',
  `start_time` date NOT NULL COMMENT '有效期',
  `end_time` date NOT NULL COMMENT '有效期',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;

