DROP TABLE IF EXISTS `rw_backend_user`;
CREATE TABLE `rw_backend_user` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '用户id',
  `user_name` varchar(255) DEFAULT NULL COMMENT '后台用户名字',
  `password` varchar(255) DEFAULT NULL COMMENT '密码',
  `create_time` varchar(255) DEFAULT NULL COMMENT '创建用户的时间',
  `login_ip` varchar(255) DEFAULT NULL COMMENT '用户最后一次登录的ip',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;

