-- ----------------------------
-- Table structure for `rw_book`
-- ----------------------------
DROP TABLE IF EXISTS `rw_book`;
CREATE TABLE `rw_book` (
  `book_id` int(10) NOT NULL AUTO_INCREMENT COMMENT '图书编号',
  `class_id` smallint(6) NOT NULL COMMENT '分类id',
  `title` varchar(100) NOT NULL COMMENT '书名',
  `search_title` varchar(100) NOT NULL COMMENT '书名拼音',
  `author` varchar(50) NOT NULL COMMENT '作者',
  `search_author` varchar(100) NOT NULL COMMENT '作者拼音',
  `cover` varchar(100) NOT NULL COMMENT '图书封面',
  `large_cover` varchar(100) NOT NULL,
  `score` float(2,1) NOT NULL,
  `isbn13` char(50) NOT NULL COMMENT '国际标准图书编号',
  `pages` smallint(6) NOT NULL,
  `pubdate` varchar(10) NOT NULL,
  `publisher` varchar(50) NOT NULL COMMENT '出版社名称',
  `intro` text NOT NULL COMMENT '图书简介',
  `price` smallint(6) NOT NULL COMMENT '定价,即书币',
  `binding` varchar(10) NOT NULL,
  `is_curl` tinyint(2) NOT NULL,
  `create_time` datetime NOT NULL COMMENT '创建时间',
  PRIMARY KEY (`book_id`),
  KEY `book_name` (`title`),
  KEY `author` (`author`),
  KEY `isbn` (`isbn13`)
) ENGINE=MyISAM AUTO_INCREMENT=2957 DEFAULT CHARSET=utf8 COMMENT='图书表';

-- ----------------------------
-- Records of rw_book
-- ----------------------------

-- ----------------------------
-- Table structure for `rw_book_pack`
-- ----------------------------
DROP TABLE IF EXISTS `rw_book_pack`;
CREATE TABLE `rw_book_pack` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `book_id` int(10) NOT NULL,
  `book_title` varchar(50) NOT NULL,
  `title` varchar(300) NOT NULL COMMENT '拆书包标题',
  `content` mediumtext NOT NULL COMMENT '拆书包内容',
  `abstract` text NOT NULL COMMENT '拆书包摘要，建议不超过200个字',
  `digg_count` smallint(6) unsigned NOT NULL,
  `comment_count` smallint(6) NOT NULL,
  `read_count` smallint(6) NOT NULL DEFAULT '0' COMMENT '阅读量',
  `create_time` datetime NOT NULL,
  `pb_id` int(10) NOT NULL COMMENT '共读id,plan_book主键',
  `target` varchar(50) NOT NULL COMMENT '建议读到多少页',
  `time_type` tinyint(2) NOT NULL DEFAULT '1' COMMENT '1早读，2晚读',
  `pub_time` datetime DEFAULT NULL COMMENT '发布时间',
  `media` varchar(150) NOT NULL COMMENT '音频地址，oss的存储',
  `media_time` varchar(10) NOT NULL COMMENT '音频时长,05:10',
  `name` varchar(10) NOT NULL COMMENT '主播名字',
  `top_img` varchar(100) NOT NULL COMMENT '置顶图片',
  `share_url` varchar(200) NOT NULL COMMENT '拆书包微信分享地址',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=90 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

-- ----------------------------
-- Records of rw_book_pack
-- ----------------------------

